mapper.select:
select

from yxtc_framework_menuinfo
where state='1'

    and id = #{id,jdbcType=INTEGER}


    and menuid = #{menuid}

order by ordernum

mapper.insert:
insert into yxtc_framework_menuinfo (id, menuinfoname, ordernum, 
  describes, menuid, url, 
  state, createdatetime, updatedatetime
  )
values (#{id,jdbcType=INTEGER}, #{menuinfoname,jdbcType=VARCHAR}, #{ordernum,jdbcType=INTEGER}, 
  #{describes,jdbcType=VARCHAR}, #{menuid,jdbcType=VARCHAR}, #{url,jdbcType=VARCHAR}, 
  #{state,jdbcType=VARCHAR}, #{createdatetime,jdbcType=VARCHAR}, #{updatedatetime,jdbcType=VARCHAR}
  )
